css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("soz6.com") {
  body {
    background-color: #272B30 !important;
    color: #C2C2C7 !important;
    font-size: 13px !important; 
    line-height: 20px !important;
    font-family: Helvetica, Arial, Sans-Serif;
  }
  
   body a {
    color: #c1ccbd !important;
  }

  .logo img  {
    margin-left: 0 !important;
  }
 
  .nav {
    margin-top: 45px !important;
    padding: 0 !important;
  }
  #header {
    top: 0;
    left: 0;
    box-shadow: 0 0 2px #C2C2C7 !important;
    position: fixed;
    background: #363B42 !important;
    height: 70px;
    margin-bottom: 20px !important;
  }

  #header a {
    color: #c0cec0 !important;
  }
  #header a:hover {
    color: #51c1b7 !important;
  }

  #header > .nav > .liste a {
     font-weight: 600 !important;
  }
  .leftfirst {
    left: 0;
    padding-left: 1% !important;
    padding-right: 1% !important;
    width: 18% !important;
    
  }
  #content {
    width: 59% !important;
  }
  .rightcolumn {
    width: 18% !important;
    padding-left: 1% !important;
    padding-right: 1% !important;
  }

  #rightpanel {
    padding-top: 32px;
  }
  #main {
    padding-top: 30px !important;
    margin-left: 2% !important;
    font-size: 14px;
  }
  #main > #chargermain div {
    color: #c0cec0 !important;
    
  }
  h3 {
    color: #66bb6a !important;
    text-decoration: none;
    font-family: sans-serif;
    font-size: 13px;
    text-shadow: none !important;
    font-weight: 700;
  }
  #chargermain > a, #chargermain > h1 > a, #chargermain > h2, .entry > a {
    color: #66bb6a !important;
  }


  .infoauth > a, .entrybuttons > a {
    color: #81c784 !important;
  }
  .infoauth > a:hover, .entrybuttons > a:hover {
    color: #a5d6a7 !important;
  }

  #leftpanel {
     padding-top: 28px !important;
  }
  #headerfinder {
    color: #424242 !important;
    background-color: #D4D7DB !important;
  }
  
  #aciklama, #mcontent {
    background-color: #6C6F74 !important;
  }
  .entry {
    font-size: 14px;
    background-color: #272B30 !important;
  }
  .mesajdaha, .mtmessage {
    color: #c8e6c9 !important;
    background-color: #373c42 !important;
  }

   input {
    background-color: #e0f2f1; /* Green */
    border: none;
    color: #4CAF50;
    text-decoration: none;
    display: inline-block;
    font-size: 13px;
  }
  .gbutton, .kanal {
    background-color: #4CAF50; /* Green */
    border: none;
    color: white;
    text-decoration: none;
    display: inline-block;
    font-size: 13px;
  }
  #tabsH > ul > li a span{
    background-color: #009688 !important;
    color: #a7ffeb !important;
  }
  #leftpanel a:hover {
    background: #009688 !important;
  }
  .yaz {
    background: #004d40 !important;
  }
  #chargermain>div>a, .sonses {
    color: #5C91BA !important;
  }
}
