css:
@-moz-document url(https://strawpoll.de) {
.answer {
    background-color: #fff;
    color: #444;
    margin: 1em 0;
    border-bottom: 1px solid #DDD;
}

a {
    color: #f4f4f4;
    outline: 0;
    text-decoration: none;
}

.orangeBold {
    font-weight: 600;
    color: #9575CD;
}

body {
    margin: 0;
    padding: 0;
    height: 100%;
    width: 100%;
    font-family: 'Droid Sans', 'Helvetica Neue', Helvetica, sans-serif;
    font-weight: 300;
    font-size: 16px;
    background-color: #212121;
    color: #f4f4f4;
}

.container {
    background-color: #2F2F2F;
    color: #f4f4f4;
    margin: 0 auto;
    max-width: 960px;
    width: 90%;
    padding: 1em;
    margin-bottom: 1em;
    -webkit-box-shadow: 0 1px 2px rgba(0,0,0,.2);
    -moz-box-shadow: 0 1px 2px rgba(0,0,0,.2);
    box-shadow: 0 1px 2px rgba(0,0,0,.2);
}

.answer {
    background-color: #fff;
    color: #444;
    margin: 1em 0;
    border-bottom: 1px solid #DDD;
}

.redBold {
    font-weight: 600;
    color: #9575CD;
}

::-webkit-scrollbar-thumb {
    -webkit-border-radius: 10px;
    border-radius: 10px;
    background: #9575CD;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5);
}
}
