css:
@-moz-document domain("www.cloudsdale.org") {

  body {

    background-color: #444 ;

    color: #DD0FF0 !important;

  }

  .chat-message {

    border-top: rgba(0, 0, 0, 0.6) solid 1px;

    border-bottom: rgba(0,0,0,0.1) solid 1px;

  }

  .chat-footer {

    background-color: #333 !important;

  }

  .chat-footer form textarea {

    color: #ffffff; /* Text Color in Type Box*/

    /*background-color: rgba(0, 0, 0, 0.1); !important; */

  }

  .container-fluid {

    background-color: #2c2c2c !important;

  }
  .sidebar-header {
    padding: 2px 7px;
    background-color: #222222;
    color: #845;
    background-image: url("http://www.static.tumblr.com/034a6c77e33ba10dbe9f3bf15665df16/ux8tjig/9YJoa2zbc/tumblr_static_e9cdfewxs88o4s4s0wk44g88k.jpg") !important;
    border-bottom: 1px solid #29303c;
    border-top: 1px solid rgba(255,255,255,0.05);
    font-size: 12px;
    text-align: center;
    text-transform: uppercase;
    position: relative;
}
  
  .sidebar-item
  {
    color: #222222;
    background-image: url("http://www.static.tumblr.com/034a6c77e33ba10dbe9f3bf15665df16/ux8tjig/9YJoa2zbc/tumblr_static_e9cdfewxs88o4s4s0wk44g88k.jpg") !important;
  }
  body .sidebar {

    background-color: #222 !important;

  }
  .cloud-head-inner p, .cloud-head-inner h2 {
    display: block;
    margin-left: 10px;
    color: #00ee08 !important;
    text-align: center;
    text-shadow: 1px 1px 1px #333;
}
  .btn 
  {
    background-color: #666 !important;
    background-image: url("http://www.static.tumblr.com/034a6c77e33ba10dbe9f3bf15665df16/ux8tjig/9YJoa2zbc/tumblr_static_e9cdfewxs88o4s4s0wk44g88k.jpg") !important;
  }
  .cloud-head-inner
  {
    background-color: #222 !important;
    background-image: url("http://www.static.tumblr.com/034a6c77e33ba10dbe9f3bf15665df16/ux8tjig/9YJoa2zbc/tumblr_static_e9cdfewxs88o4s4s0wk44g88k.jpg") !important;
  }
  h2
  {

    background-color: #222 !important;

    

  }

  p

  {

    color: white !important;

  }

  .chat-message-content.expanding-init

  {

    background-color: #333 !important;

    color: white !important;

  }

  .chat-message-form {

    background-color: #222 !important;

    color: #333 !important;

  }

  div.chat-footer

  {

    color: white;
     background-image: url("http://www.static.tumblr.com/034a6c77e33ba10dbe9f3bf15665df16/ux8tjig/9YJoa2zbc/tumblr_static_e9cdfewxs88o4s4s0wk44g88k.jpg") !important;

  }

  

  .self-reference {

    background-color: #333 !important;

}

  .role-owner

  {

    color: pink !important;

  }
  
  /*OVERRIDES NAME COLORS*/
  a {
    color: #6cff08 !important;
    /*background-color: #333 !important;*/
}
  li.sidebar-item > a {
    display: block;
    padding: 7px;
    background-color: #333 !important;;
    background-image: url("http://www.static.tumblr.com/034a6c77e33ba10dbe9f3bf15665df16/ux8tjig/9YJoa2zbc/tumblr_static_e9cdfewxs88o4s4s0wk44g88k.jpg") !important;
}

  

}
