css:
@-moz-document domain("havenandhearth.com") {
body {
    text-shadow: 0px 0px 2px rgba(0,0,0, 1);
}
.bg1 {
    background: #403020;
    color: #fff;
}
.bg2 {
    background: #504030;
    color: #fff;
}
.post:target .content {
    color: #fff;
}
blockquote {
    background: #908040;
}
a:visited { color:#bbc; }
a:link { color:#ddf; }

.postbody { color:#ccc; }
.postprofile { color:#ccc; }
.content { color: #ccc; }
}
