@-moz-document domain("newz.dk") {
 div.secondary_column {
  width: 300px !important;
  float: right !important;
  margin: 0 0px 70px 0 !important;
  background-image:none;
 }

/* Advertisements */
 div[class^="ad"], 
 #sweeplist,
 #left_placeholder,
 .newsletter,
 .promotion,
 .gfx1
{
   display:none!important
}

 html > body #wrapper {
  width: 95%!important;
  max-width: 11100px!important;
  min-width: 10px!important;
  }

  div.primary_column {
    width: 65% !important;
  }
  
  #top, #container {width: 95%!important}

  .news, .news h1, .news p {width: 100%!important;}

  .news_coloum {margin-left: 20px!important}

  /* ================ COMMENTS ===================== */
  .comments, .comment, .comment_content p, .text_comment 
 {
   width: 100%!important
 }

  /* ================ PAGINATION ==================== */
  .pagination .current {
    font-size: 260%;
    background: red !important;
  }

  .pagination a,
  .pagination a:visited,
  .pagination a:active {
    font-size: 260%; 
  }


}
