css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.cnn.co.jp") {
  .container {
      min-width: 980px;
      width: 100%;
  }

  #left-column {
    min-width: 640px;
    width: calc(100% - 340px);
  }

  #leaf-body p {
    padding: 0 24px 19px 24px;
  }
  .top-main-column {
    min-width: 670px;
    width: calc(66% - 20px);
  }
  .span-sl-left {
    min-width: 250px;
    width: calc(40% - 20px);
  }
  .span-sl-mid {
    min-width: 415px;
    width: calc(60% - 20px);
  }
  .top-right-column {
    min-width: 305px;
    width: calc(33% - 20px);
  }
  .channel-row {
    min-width: 990px;
    width: 100%;
  }
  .channel-box {
    min-width: 320px;
    width: calc(33% - 20px);
  }
  #channel-sub-stories .story-box {
    min-width: 280px;
    width: calc(50% - 20px);
  }

  .photo-box li {
    min-width: 120px;
    width: calc(25% - 30px);
  }
}
