/* AGENT_SHEET */

/*-----[ Browser ScrollBar BY sSolov ]-----*/

/*----- Firefox -----*/
scrollbar
{
  -moz-appearance: none !important;
  background: none !important;
  background-color: transparent !important;
  position: relative !important;
}
scrollbar scrollbarbutton
{
  display: none;
}

scrollbar[orient="vertical"]
{
  margin-left: -7px !important;
  min-width: 7px !important;
  max-width: 7px !important;
}
scrollbar[orient="horizontal"]
{
  margin-top: -7px !important;
  min-height: 7px !important;
  max-height: 7px !important;
}

scrollbar thumb
{
  -moz-appearance: none !important;
  border: none !important;
  border-radius: 0 !important;
  background: transparent !important;
}

scrollbar[orient="vertical"] thumb
{
  min-height: 100px !important;
  border-right: 2px solid rgba(0,150,200,0.6) !important;
}
scrollbar[orient="horizontal"] thumb
{
  min-width: 100px !important;
  border-bottom: 2px solid rgba(0,150,200,0.6) !important;
}

scrollbar[orient="vertical"]:hover thumb
{
  border-top: 7px solid rgba(0, 0, 0, 0) !important;
  border-bottom: 7px solid rgba(0, 0, 0, 0) !important;
  border-right: 7px solid rgba(250,150,0, 0.6) !important;
}
scrollbar[orient="horizontal"]:hover thumb
{
  border-left: 7px solid rgba(0, 0, 0, 0) !important;
  border-right: 7px solid rgba(0, 0, 0, 0) !important;
  border-bottom: 7px solid rgba(250,150,0, 0.6) !important;
}

scrollbar[orient="vertical"]:active thumb
{
  border-right: 7px solid rgba(250,150,0, 0.4) !important;
}
scrollbar[orient="horizontal"]:active thumb
{
  border-bottom: 7px solid rgba(250,150,0, 0.4) !important;
}
