css:
@-moz-document domain(eksisozluk.com) {

#logo a {background-image: url(http://static.tumblr.com/itoo6en/lTOojb86c/eksilogo.svg);}
.eksiseyler-logo {background-image: url(http://static.tumblr.com/itoo6en/lQcojbgba/eksiseyler_logo.svg);}

.light-theme #advanced-search-form, .light-theme .dropdown .dropdown-menu, .light-theme .popped-player, body.light-theme, body.light-theme>header, .light-theme .modal, .light-theme #in-topic-search-options {background-color:#fdfdfd!important;}
#sub-navigation>ul>li>a {padding:0 20px 0!important;margin:0!important;}
#top-navigation .dropdown-toggle:after {content:"menü"!important;}
.tabs>li.active {border-bottom-color:transparent!important;}
#entry-list .hidden, #entry-list .deleted {font-style:italic!important;}
.editform {background-color:transparent!important;}
.editform fieldset {padding:0!important;}
.edittextbox.with-helpers {min-height:300px!important;}
#top-navigation>ul .new-update .eksico, #sub-navigation>ul .new-update .eksico {fill:#fff!important;}
#top-navigation>ul .new-update, #sub-navigation>ul .new-update {color:#fff!important;background-color:#80c14b!important;}
#threads li article.unread h2 {color:#80c14b!important;}
.loggedin #sub-navigation>ul {display:inline-block!important;}
.loggedin #sub-navigation>ul>li {width:auto!important;}
.topic-list li>a.empty-index-item {color:#bbb!important;}
#entry-list a, #draft-content a {color: #118dff;}
body>header {border-top: 4px solid #118dff;}
#search-form #search-textbox {border: 1px solid #118dff;}
#search-form button[type=submit] {background-color: #118dff;}
button.primary, input[type=submit].primary, input[type=button].primary {border: 1px solid #118dff;background-color: #118dff;}
button.primary:hover,input[type=submit].primary:hover,input[type=button].primary:hover,button.primary:focus,input[type=submit].primary:focus,input[type=button].primary:focus{background-color:#349eff;}
html.no-touch #sub-navigation>ul>li:hover>a, html.no-touch #sub-navigation>ul>li.active>a {color: #118dff;border-color: #118dff;}
#top-navigation>ul .new-update, #sub-navigation>ul .new-update {color: #fff!important;background-color: #118dff!important;}
#entry-list footer .other .eksico {fill: #118dff;}
#who-to-follow>.nick-list>li>a {color: #118dff;}
#top-navigation .dropdown-toggle:after {content: "menü"!important;display: none;}
.mobile-notification-icons>ul .new-update {background-color: #118dff;}
body.light-theme.theme-disabled #top-bar .eksiseyler-logo-mobile .eksico {fill: #118dff;}


#header .header-logo a {background-image: url(http://static.tumblr.com/itoo6en/lQcojbgba/eksiseyler_logo.svg);}

}
