css:
@-moz-document 
  url-prefix("http://chat.thecolorless.net/#"),
  url-prefix("http://chat.thecolorless.net/index"), 
  url-prefix("http://chat.thecolorless.net/winter") {
  
  #header {
    background-color: #222223;
  }
  
  a.dropdown-toggle {
    background-color: #8F402D;
  }
  a.dropdown-toggle:hover {
    background-color: #DF6447;
  }
  
  #header #nav a.logo {
    color: #444;
  }
  
  #header input.message {
    background-color: #ccc;
  }
  #header input.message:focus {
    border: 3px solid #8F402D;
  }
  
  #header form button {
    background-color: #aaa;
    cursor: pointer;
  }
  #header form button:hover {
    background-color: #bbb;
    border: 3px solid #8F402D;
  }
  
  .subscription .text .article {
    filter: brightness(0.9) !important;
  }
  
}
