css:
@-moz-document url-prefix("https://www.3dmgame.com/"){
body{
    background:#e5e5e5;
}
#index_bg_box,
.tonglona_2,
.tonglona_3,
.tonglona_4,
.Tonglan,
#index_top_tonglan{
   display:none;
}
.content{
    margin-top:0px!important;
    background: #e5e5e5;
}
/* .Min_2 .Min2_R .near_wrap{
    margin-top: -320px;
    background: #fff;
    position: relative;
} */
}
@-moz-document regexp("(https://www.3dmgame.com/)((news)|(vote))+.+"){
.commentswrap,
.fotter,
.nav,
.pin,
.header_top,
.search,
.crumb{
    display:none;
}
.news_warp_center{
	margin:30px 0 0 0;
}
.news_warp_center p{
	font-size:18px;
	line-height:35px;
}
.news_warp_top .bt{
	font-size:28px;
	text-shadow: 4px 4px 8px #bdbdbd;
	margin:10px 0 50px 0;
}
.time{
	margin-left:80px;
}
.Content_L {
	margin-left:173px;
}


.Content_R,
.dj_warp_a,
.dj_warp_b,
.dj_warp_c,
.dj_warp_d,
.dj_warp_e,
.dj_warp_f,
div[style='width:346px;height:288px; overflow: hidden;  margin-bottom: 20px;'],
div[style='width:346px;height:226px; overflow: hidden;  margin-bottom: 20px;']{
	display:none;
}
}
