css:
@-moz-document domain("www.tumblr.com") {
.messaging-conversation--open.theme-background-dark .conversation-compose {
	padding: 5px 0;
}
.messaging-conversation--open .conversation-compose .submit-button {
    padding: 0;
    width: 44px;
}
.messaging-conversation--open .conversation-compose .submit-button .submit {
    height: 100%;
    vertical-align: middle;
    width: 44px;
    padding: 9px 10px;
}
.messaging-conversation--open .conversation-compose .conversation-compose-plugins-container {
	flex-direction: column;
    padding-left: 10px;
    height: 58px;
    margin-top: auto;
    margin-bottom: auto;
}
.messaging-conversation--open .conversation-compose .conversation-compose-plugins-container .conversation-compose-plugin+.conversation-compose-plugin {
	margin-left: 0;
    margin-bottom: 0;
}
.messaging-conversation--open .conversation-compose .text-input-container .text-input {
	/*padding-left: 5px;*/
    padding-right: 2px;
}
}
