css:
@-moz-document domain("ishadijcks.github.io") {
body, #townView {
    background-image: none !important;
}

#mapDiv, #enemy, #pokeball, #dungeonPokeball, #oakItemBody, #gymEnemy, #dungeonEnemy, #eggBody {
    opacity: 0;
}

#mapDiv:hover, #enemy:hover, #pokeball:hover, #dungeonPokeball:hover, #oakItemBody:hover, #gymEnemy:hover, #dungeonEnemy:hover, #eggBody:hover {
    opacity: 1;
}

.progress-bar, #console, #pokemonBody img, #itemBody img, #gymTrainer > img:nth-of-type(1) {
    display: none;
}

.eggProgress .progress-bar {
    display: block;
}
}
