@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
   
    .tweet .IconTextContainer {
        display: none;
    }
    
    .ProfileCardStats-statList {
        display: none;
    }
    
    .ProfileNav-value {
        display: none;
    }
    
    div[data-activity-type="favorite"] {
       display: none;
    }
    
    div[data-activity-type="follow"] {
       display: none;
    }
        
    div[data-activity-type="retweeted_retweet"] {
       display: none;
    }
    
    div[data-activity-type="retweet"] {
       display: none;
    }
    
    .ProfileTweet-actionCount {
        display: none;
    }
    
    div[data-activity-type="favorited_retweet"] {
        display: none;
    }
    
    div[data-activity-type="retweeted_mention"] {
        display: none;
    }
        
    div[data-activity-type="favorited_mention"] {
        display: none;
    }

    .global-nav .people .count .count-inner {
        display: none;
    }

    .trend-item-stats {
        display: none;
    }
    
    .TweetImpressionsModule {
        display: none;
    }
    
    .js-tweet-stats-container {
        display: none;
    }
        
}
