css:
@-moz-document url-prefix("http://www.fakaheda.eu/") {
p, h1, h2, h3, h4, h5, h6, dd, dl, dt, td, li, em, td.name {
	color: cyan !important;
}
a, strong {
	color: gold !important;
}
td, tr, thead, table, label, .text, dt {
	color: lime !important;}
pre {
	color: gray !important;
}
#body.with-carousel, #body.cp, h1, h3, h4, h5, h6, p, div, dd, dl, ul, td, #body, .decoration, #carousel-overlay{
	background: #1c1c1c url(http://www.colorhexa.com/1c1c1c.png) !important;
	border-color: cyan !important;
}
button, .filterinput {
	background: green url(http://www.colorhexa.com/00ff00.png) !important;
}
.menu-container.white h2, .square-box.box.cp-box h2, .article h2, h2.pretty-title, input {
	background: #420000 url(http://www.colorhexa.com/420000.png) !important;
	border: 1px cyan !important;
}
.console-output {
	background: #000000 url(http://www.colorhexa.com/000000.png) !important;}}
