css:
@-moz-document domain("kaniwani.com") {
.bg-image {
-webkit-filter:invert(1)    
}

.site-content {
 	background-color: rgb(67, 72, 88);
}

.user-section {
	background: none;
}
.site-header {
    background: #4d5262;
    background-image: linear-gradient(to bottom, rgb(82, 87, 103), rgb(77, 82, 98)) !important;
}

.announcements-section {
     background-color: rgb(67, 72, 88);
}

h1, h2, p, span, label, li,
.section-heading>a,
.section-heading>.breadcrumb,
.content-inner,
.announcements-section .title,
.section-heading {
    color: rgb(225,225,225);
}

.accordion-content {
    background-color: #4d5262;
}

.accordion-list {
    border: 2px solid #434858;
}

.accordion-list dd+dt {
    border-top: 2px solid #434858;
}

.reveal-block>.reveal>.text {
    background-color: hsl(0, 0%, 25%);
}
.reveal-block>.button {
    border: 1px solid #6e6e6e;
}

.nav-link>.text .count {
    color: #a7cc0d;
}

body {
    background: #2f2f2f;
}

.review-bg {
    background-color: #2f2f2f;
}

.summary-section>.inner .section-heading {
    color: rgb(225,225,225);
}
}
