css:
@-moz-document url("http://www.wuxiaworld.com/"), url-prefix("http://www.wuxiaworld.com/"), regexp("http://www.wuxiaworld.com/") {
.widget-title {
    background: #2e2d2d !important;
    color: #ddd !important;
}
.featured-title {
    background: #2e2d2d !important;
    color: #ddd !important;
}
.chapterBtn * {
    background: #2e2d2d !important;
    color: #ddd !important;
    border-bottom: 1px solid #ccc !important;
}
div[class^="wp-embed"] {
    background: #2e2d2d !important;
    color: #ddd !important;
}
div[class^="wp-embed"] * a {
    color: #ddd !important;
}
.ww_elapsed_time::after {
    filter: invert(100%) !important;
    -webkit-filter: invert(100%) !important;
}
}
