css:
@-moz-document url-prefix("https://www.paybox.kz/admin"),
url-prefix("https://paybox.kz/admin"),
url-prefix("https://paybox.money/admin") {

  .nav.nav-second-level.collapse {
    display: block;
    height: auto !important;
  }
  
  #side-menu > li > .title_menu {
    font-weight: bold;
    font-size: 1.2em;
    color: black;
  }
  
  .nav > li > a {
    padding: 7px 15px;
  }
  
}

@-moz-document url-prefix("https://paybox.kz/admin/"),
url-prefix("https://www.paybox.kz/admin/"),
url-prefix("https://platron.ru/admin/"),
url-prefix("https://www.platron.ru/admin/"),
url-prefix("https://billinger.ru/control/"),
url-prefix("https://www.billinger.ru/control/") {
  
  body, p, th, td, div, input, textarea, select, option, li, ul, ol, dt, dd {
    font-size: 1em;
    font-family: Tahoma, Arial, Helvetica, sans-serif;
  }
  
  body {
    font-size: 13px;
  }
  
  #menu div[class^="menu"] {
    font-size: 1em;
    line-height: 1.75em;
  }
  
}

@-moz-document url-prefix("https://paybox.kz/admin/"),
url-prefix("https://www.paybox.kz/admin/"),
url-prefix("https://platron.ru/admin/"),
url-prefix("https://www.platron.ru/admin/") {
  
  #logon_holder table td {
    font-size: 1em;
  }
  
  table.canvas {
    width: 100%;
  }
  
  div.main {
    width: auto;
    padding: 0 16px;
  }
  
  /* Срок жизни транзакции истек */
  #content TABLE.rounded TR.expired TD {
    background-color: #E3E3E3;
  }
  
}

@-moz-document url-prefix("https://billinger.ru/control/"),
url-prefix("https://www.billinger.ru/control/") {
  
  #path {
    font-size: 1em;
  }
  
  #content TABLE[border="1"] TD {
    padding: 2px 4px;
  }
  
  H1 {
    font: inherit;
    font-weight: bold;
  }
  
  body {    
    background-color: #c0c0c0;
    padding: 16px !important;
  }
  
  body > table {
    background-color: white;
    border-radius: 16px;
  }
  
  body > table > tbody > tr > td:first-child { padding-left:   28px !important; }
  body > table > tbody > tr > td:last-child  { padding-right:  28px !important; }
  body > table > tbody > tr:first-child > td { padding-top:    28px !important; }
  body > table > tbody > tr:last-child  > td { padding-bottom: 28px !important; }
  
  body > div[align="right"] {
    position: relative;
  }
  
  body > div[align="right"] > table {
    position: absolute;
    top: 36px;
    right: 48px;
  }
  
  a[href^="/auth/?act=logout&"] {
    text-decoration: none;
    display: inline-block;
    line-height: 58px;
    width: 58px;
    background-color: #E3E3E3;
    color: #0090D6;
    text-align: center;
    border-radius: 100%;
  }
  
}

@-moz-document url-prefix("https://teddyid.com/control/"),
url-prefix("https://www.teddyid.com/control/") {

  .b-main-menu--left .b-main-menu__item {
    font-size: 13px;
    line-height: 1.75em;
  }
}
