css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tutanota.de"), domain("tutanota.com") {

  #innerMailFolderColumn, #settingsInnerColumn, #contactList, #conversationColumn div.buttonBar, #contactColumn div.buttonBar {
    background : linear-gradient(to bottom, #C92424, #831F22) !important ;
  }  
  div.menuList, body div#searchAndContactListColumn > div#searchAndContactList section#contactList > div#innerContactList {background : none !important ;}
  
  .mailFolders .fontImage, #settingsInnerColumn .menuItem, #conversationColumn .fontImage, #contactColumn .fontImage {color : white !important ;}
  
  .menuItem.active, .menuItem.dragOver, .menuItem.selected, .menuItem:active {background : #515D6E !important ;}
}
