css:
@-moz-document domain("yify-movies.net")
{
 html,
body,
body.thumbnail,
div[class="background-container"] {
	background-color: #000 !important;
	background: url("http://res.cloudinary.com/mrquinn/image/upload/v1465660031/styles/lt_wood_bkg.jpg") fixed repeat !important;
	color: #FFE081 !important;
	border-color: #A48553 #C7B188 #C7B188 #A48553 !important;
}
div[class="logo"],
div[class="rblock"] h3 {
	display: none!important;
}
div[class="header"],
div[class="headertop_desc"],
div[class="content"],
div[class="footer"],
div[class="section group"] {
	background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652799/styles/trans_back.png")!important;
	background-repeat: repeat-y !important;
}
h3,
div[class="content_top"],
div[class="content_bottom"],
div[class="header_bottom_left"],
div[class="nav_list"],
div[class="account_desc"] {
	background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460677417/styles/main_nav_bg.gif") !important;
	background-color: #ffe081;
	border: none !important;
}
div[class="product_desc"],
div[class="desc span_3_of_2"] {
	background-color: #fff !important;
}
div[class="search_box"] {
	background: url("http://res.cloudinary.com/mrquinn/image/upload/v1460652799/styles/trans_back.png")!important;
}
h2,
p {
	color: #000 !important;
}
a:link,
a:visited {
	color: #000 !important;
}
div[id="reviews"],
div[class="product_desc"] {
	padding: 15px;
}
}
