css:
@-moz-document domain("slither.io") {
/*Version 2.3*/

#logo, #clq, #twth, #fbh, #social, #fb-root, #title-hud.nsi, .btnt .sadd1, #adContainer, .nsi[style*="width: 255px;"], #ocho
{display:none!important}

.chatovodWindow
{opacity:.9}

#nbg
{background-image:none!important}

.chatovodButton
{
  border-color:transparent;
  left:10px!important;
}

body
{background:#171717!important}

.col-xs-12.col-md-8 div
{background:transparent!important}


#login div
{box-shadow:none!important}

.sumsginp, .nsi
{color:white!important}

input[id="nick"]::-webkit-input-placeholder
{color:gray!important}


.taho, #taho, .btnt.nsi.sadg1, .btn-primary, .btn-info, .form-control
{
  background:#111!important;
  border-radius:4px!important;
  box-shadow:none!important;
  border: none
}

.btnt.nsi.sadg1:hover, .sadu1, .btn-primary:hover, .btn-info:hover, .form-control:active
{background:#171616!important;}

iframe[src*="social.html"]
{ top:0px!important;
  left:0px!important; }
}

@-moz-document domain("slither-io.com") {
.fb-page.fb_iframe_widget
{display:none}
}
