css:
@-moz-document regexp("^.*www.gnpcb.org/esv/mobile/.*$") {
body {
    background: black !important;
    color: gray;
    text-align: justify;
    font-family: sans-serif;
}

h3 {
    font-variant: small-caps;
    font-style: oblique;
}

.verse-num {
    vertical-align: super;
    font-size: x-small;
}
.woc {
    color: red;
}

@media (min-width: 600px) {
    .esv-text {
        -webkit-column-count: 2; /* Chrome, Safari, Opera */
    	-moz-column-count: 2; /* Firefox */
    	column-count: 2;
    	-webkit-column-rule-width: 0.1em; /* Chrome, Safari, Opera */
    	-webkit-column-rule-style: outset;
    	-moz-column-rule-width: 0.1em; /* Firefox */
   	 	-moz-column-rule-style: outset;
    	column-rule-width: 0.1em;
 		column-rule-style: outset;
    	-webkit-column-rule-color: gray; /* Chrome, Safari, Opera */
    	-moz-column-rule-color: gray; /* Firefox */
    	column-rule-color: gray;
    }
}
}
