css:
@-moz-document url-prefix("http://my.safaribooksonline.com"), url-prefix("https://www.safaribooksonline.com") {
#HtmlView .epub__text,
#HtmlView p.epub__paraaftertitle,
#HtmlView p.epub__para {
    text-align: left;
}
#HtmlView h1.epub__subtitle,
#HtmlView h2.epub__subtitle,
#HtmlView h3.epub__subtitle {
    font-family: Tahoma, Arial, helvetica, "san-serif"
}
#HtmlView code.epub__literal,
.htmlcontent pre,
.htmlcontent tt,
#HtmlView pre.epub__codesnippet,
#HtmlView span.epub__inlinecode #lesson-fragment .pre,
#lesson-fragment .pre-ex,
#lesson-fragment .pre1,
#lesson-fragment .pre_w,
#lesson-fragment p.pre,
#lesson-fragment pre,
#sbo-rt-content .pre,
#sbo-rt-content .pre-ex,
#sbo-rt-content .pre1,
#sbo-rt-content .pre_w,
#sbo-rt-content p.pre,
#sbo-rt-content pre {
    font-family: "Droid Sans Mono", Consolas, "Lucida Console", Monaco, monospace;
}
.htmlcontent tt,
#HtmlView span.epub__inlinecode {
    font-size: inherit ! important;
    color: darkblue;
}
#HtmlView pre.epub__codesnippet {
    font-size: smaller;
    color: darkred;
}
}

@-moz-document url-prefix("http://my.safaribooksonline.com") {
#HtmlView p.epub__paraaftertitle,
#HtmlView p.epub__para,
#HtmlView p.epub__normal {
    text-align: left;
}
#HtmlView .epub__h1,
#HtmlView .epub__h2,
#HtmlView .epub__h3,
#HtmlView .epub__h4,
#HtmlView .epub__h5,
#HtmlView .epub__h6,
#HtmlView .epub__h7,
#HtmlView .epub__h8 {
    font-family: sans-serif;
}
html[mode="night"] img,
html[mode="night"] i,
html[mode="night"] video {
    -webkit-filter: none !important;
}
#HtmlView code.epub__literal,
.htmlcontent pre,
.htmlcontent tt,
#HtmlView pre.epub__codesnippet,
#HtmlView span.epub__inlinecode {
    font-family: Consolas, "Lucida Console", Monaco, monospace;
}
.htmlcontent tt,
#HtmlView span.epub__inlinecode {
    font-size: inherit ! important;
    color: darkblue;
}
#HtmlView pre.epub__codesnippet {
    font-size: smaller;
    color: darkred;
}
}
