@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("golem.de") {

  /* Zu verwenden in Kombination mit NoScript (golem.de erlauben) */
    
  /* Cookie Hinweis */
  #golem-cookie-accept {
    display:none!important;
  }
  
  /* Werbung */
  .adsbygoogle, DIV[style="margin:16px 0; min-height:266px; clear:both;"], DIV[style="min-height: 282px;"], #label4iqadtile8, #pricehits, IFRAME, #bannerTopWrapper, #iqadtile1, #iqadtile2, #iqadtile3, #iqadtile4, SPAN[style="display:block; position:relative; padding-bottom:60%; padding-top:0px; height:0; overflow:hidden; margin:20px 0;"], .gsap.gsapb, #gts-clip {
    display:none!important;
  }

  /* noscript Ersatzwerbung */
  NOSCRIPT {
    display:none!important;
  }
}
