@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.gameblog.fr"), domain("gameblog.fr") {
  
  div#gb_content_start.bg_blanc{
    background-color: #474747;
  }
  
  body {
    background: linear-gradient(to bottom,#212121 0%,#3e3e3e 20%);
    color: #AEAEAE;
  }
  
  .titre {
    color: #CCC;
  }
  
  .u8aQis9 {
    background-color: #474747;
  }
  
  nav.pagination .dizaines a {
    color: white!important;
    background-color: #566369;
  }
  
  h2.rubrique, h3.maintitle, h3.rubrique, .sommaire .rubrique, .listeArticles .rubrique, .blocResume .rubrique, .sondage .rubrique {
    color: #CCC;
  }
  
  #footerban.bg_blanc {
    background-color: #474747;
  }

  article footer {
    background: #353535;
  }

  article footer a {
    color: #E4E4E4
  }
  
  .EmbeddedTweet {
    background-color: #c0c0c0;
  }
  
  #u8QsKl1 .u8QsKl5 a img {
    display: none;
  }
  
  .rc-photo {
    display: none;
  }
  
  .U8sdsT15, #gb_side_top {
    display: none;
  }
  
  #gb_content_start div div iframe {
    display: none !important;
  }
  
}
