css:
@-moz-document domain(zendesk.com) {
	.comment_input .btn.track-id-publicComment.active {
		background-color: red !important;
	}

	.comment_input .btn.track-id-publicComment.active:after {
		border-top-color: red;
	}

	.comment_input textarea {
		background: #ffe6e6 !important;
		border: 2px solid red !important;
	}

	.comment_input:not(.is-public) textarea {
		background: #fffcf3 !important;
		border: 2px solid #527000 !important;
	}
}
