css:
@-moz-document domain("dn.ap-com.co.jp") {
#dn-main
{
	width: 99%;
}

.co-saturday,
.co-sunday,
.co-holiday
{
	background-color: #B9B9B9 !important;
}

.cal-day-text
{
	font-size: 18pt !important;
}

.cal-month .cal-h-week td.cal-day
{
	color: #000;
}

table.cal-h-cell
{
	background-color: #000000;
}

.co-today
{
	border-left: 3px dashed #000;
	border-right: 3px dashed #000;
	border-bottom: 3px dashed #000;
}

#tag-links,
a.sch-icon-1,
a.sch-icon-7,
a.sch-icon-31,
a.sch-icon-memo
{
	display: none !important;
}
}
