css:
/*
Author: 8cr14mov
Purpose: Darken and clean soundcloud interface, additionally, inspired by and written for a good buddy Kaffle.
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("soundcloud.com") {
  #app, .playControls__bg, body, .volume__sliderBackground, .sound__soundActions, .commentForm__wrapper, .commentForm__input, .header {
    background: #17181A !important;
  }
  .playControls__inner {
    background: #17181A !important;
    border-top: none !important;
  }
  .volume__sliderWrapper, .playbackTimeline__progressBackground, .compactTrackList__moreLink:hover, .compactTrackListItem.clickToPlay:hover, .compactTrackList__moreLink,
  .compactTrackListItem.clickToPlay.active, .dropbar__content, .sc-tag, .g-dark input[type="search"], .modal__modal, .g-modal-section, .tabs__tabs, input, .moreMenu__list, .listenLogin, .dialog, 
  .commentItem.m-creatorComment, .chartTrack:hover, .sc-background-light, .uploadMain__foot, .uploadMain__loggedOut, .uploadMain__chooserContainer, .commentFormDisabled, .profileMenu__list, .userNav__button.selected,
  .l-fixed-top-one-column>.l-top, .g-dark-bg, .unreadConversations, .trackItem.hover, .trackItem.active, .tabs__headingContainer, .sc-toggle, .sc-toggle-handle, .audibleEditForm__form, .gritter-item-wrapper, .g-upsell-container, #content > .container > .row,
  #footer > .container > .row, .sidebarInfoBox {
    background: #252525 !important;
    color: #797979 !important;
  }
  .l-footer, .mobileAppsButtons, .announcement, .g-scrollbar-visible, #content > div > div.l-messages-main > div > img {
    display: none !important;
  }
  * {
    border-color: #252525 !important; /* Maybe make these purple? */
    box-shadow: none !important;
  }
  .spotlight__item, .commentForm__wrapper, .listenEngagement {
    border: none !important;
  }
  .volume__sliderWrapper::after, .volume__sliderWrapper::before {
    border-color: transparent transparent #252525 #252525 !important;
    border-top-color: transparent !important;
    border-right-color: transparent !important;
    border-bottom-color: #252525 !important;
    border-left-color: #252525 !important;
  }
  .sc-button {
    background: #404040 !important;
    color: #999999 !important;
  }
  a.sc-link-light, .commentBadge__time, .g-type-shrinkwrap-large-primary, .g-type-shrinkwrap-large-secondary, .g-dark a, .soundTitle__uploadTime {
    color: #797979 !important;
  }
  a:hover {
    color: #454545 !important;
  }
  .soundBadge__additional, .compactTrackListItem.clickToPlay:hover .compactTrackListItem__additional, .compactTrackListItem.clickToPlay.active .compactTrackListItem__additional, 
  .truncatedUserDescription.m-overflow.m-collapsed .truncatedUserDescription__wrapper::after, .truncatedAudioInfo.m-overflow.m-collapsed .truncatedAudioInfo__wrapper::after, .trackItem.hover:not(.m-disabled) .trackItem__additional,
  .trackItem.hover:not(.m-disabled) .trackItem__additional, .trackItem.active:not(.m-disabled) .trackItem__additional, .editTrackItem.m-active .editTrackItem__additional {
    background: transparent !important;
  }
  .g-scrollbar, .g-scrollbar-vertical {
    background: #353535 !important;
    box-shadow: none !important;
    border: none !important;
  }
  
  /*
  IMAGE SWAPPING
  */
  
  .emptyNetworkPage.emptyAlbums .emptyNetworkPage__image {
    background-image: url(https://i.imgur.com/OdBeR5O.png) !important;
  }
  .paging-eof::before {
    background: url(https://i.imgur.com/kLcF8wf.png) center center no-repeat !important;
  }
  .header__navMenu > li > a.header__moreButton {
    background: url(https://i.imgur.com/5s0Gezn.png) 50% 50% no-repeat !important;
  }
  .emptyNetworkPage.emptyStream .emptyNetworkPage__image {
    background-image: url(https://i.imgur.com/QxrvZef.png) !important;
    background-size: 240px 178px !important;
  }
  .emptyNetworkPage.emptyTracks .emptyNetworkPage__image {
    background-image: url(https://i.imgur.com/ZEvSEKh.png) !important;
    background-size: 240px 167px !important;
  }
  .emptyNetworkPage.emptySets .emptyNetworkPage__image {
    background-image: url(https://i.imgur.com/OdBeR5O.png) !important;
    background-size: 240px 167px !important;
  }
  .emptyNetworkPage.emptyReposts .emptyNetworkPage__image {
    background-image: url(https://i.imgur.com/9v9Yufy.png) !important;
    background-size: 240px 167px !important;
  }
  .trackManager__noTracksImage {
    background-image: url(https://i.imgur.com/ZEvSEKh.png) !important;
    background-size: 240px 167px !important;
  }
  .emptyNetworkPage.emptyLikes .emptyNetworkPage__image {
    background-image: url(https://i.imgur.com/wDaSGXl.png) !important;
    background-size: 240px 167px !important;
  }
  .emptyNetworkPage.emptyStations .emptyNetworkPage__image {
    background-image: url(https://i.imgur.com/Ngb6kIB.png) !important;
    background-size: 200px 200px !important;
  }
  .emptyNetworkPage.emptyUsers .emptyNetworkPage__image {
    background-image: url(https://i.imgur.com/V1u0tRF.png) !important;
    background-size: 240px 177px !important;
  }
  .emptyNetworkPage.emptyHistory .emptyNetworkPage__image {
    background-image: url(https://i.imgur.com/FEhyOZz.png) !important;
    background-size: 200px 200px !important;
  }
  
  /*
  END IMAGE SWAPPING
  */
  
  .modal.modalWhiteout {
    background-color: rgba(24, 26, 27, .9) !important;
  }
  .g-dark a:hover {
    color: #999999 !important;
  }
  .header__navMenu > li > a.header__moreButton.selected {
    background-color: #17181A !important;
  }
  .staticPage p a:not(.sc-button), .staticPage li a, a, .sc-text-link, a:visited {
    color: #f50 !important;
  }
  .audibleTilePlaceholder:before {
    border: 1px solid #252525 !important;
  }
  .composeTextfield .textfield__input, .recipientChooser .tokenInput__wrapper, .tokenInput__wrapper, .moreActions__button, .sc-radio-radio, textarea, .moreActions__group .moreActions__link, .header__navMenu > li > a.selected, .editTrackItem.m-active {
    background: #353535 !important;
    color: #797979 !important;
  }
}
