css:
@-moz-document regexp(".*?rememberthemilk.com.*?") {
/* Убираем лого */

.b-Ep-Ej-Fp-Yn {
    top: 0px;
}
.b-gk-hk .b-rn-qo {
    background: none;
    display:none
}

/* Прячем покупку премиума */

.b-Ep-jo-Rj-Yn{
	display:none
}

.b-Ep-Ej-Fp-Yn {
    bottom: 0px;
}

body.jg {
    font-family: -apple-system,".SFNSText-Regular","Helvetica Neue","Hiragino Sans GB","WenQuanYi Micro Hei","Microsoft Yahei",sans-serif,Meiryo, 'Century Gothic', Ubuntu, verdana, Arial, clean, sans-serif !important;
}

/* Меняем цвет шапки */

.b-gk-hk .b-Oj {
    background: rgba(0, 0, 0, 0.8);
}

/* Меняем цвет фона сайдбара */

.b-Ep-rn
{
    background-color: #f5f5f7;
}

.b-gk-hk .b-Mj {
    background-color: #f5f5f7;
}

/* Делаем рамку между блоками тоньше */
.b-gk-hk .b-wd-aq, .b-gk-ik .b-wd-aq, .b-gk-jk .b-wd-aq {
    box-shadow: -1px 0 0 #d9d9d9;
}

/* Убираем border-radius блоков и зазор между шапкой */
.b-wd {
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    top: 0px;
}
}
