css:
@-moz-document domain("www.satorireader.com") {

/* Top nav bar */
#nav,
#nav-large,
#nav-large-user,
#nav-large #nav-large-user {
    background-color: #0078e7;
    border-bottom: none;
}

/* Top nav bar links */
#nav-large .separator.disabled,
#nav-large a.disabled {
    color: #FFFFFF !important;
}
#nav-large a.disabled:hover.disabled,
#nav-large .separator:hover.disabled {
    border-color: #FFFFFF !important;
}

/* Article series list: Coming Soon! tile */
.tile-coming-soon {
    background-color: #0078e7;
}

/* Titles */
h2,
#testimonials .testimonial p,
#testimonials .testimonial .testimonial-attribution,
.article-title,
.series-detail-grid-item .series-detail-grid-right .title a,
.article-instance-list-item-title a,
.article-set-list-item .title a,
#pro-content-message h3 {
    color: #1E90FF;
}

/* Article list: "Notes:" */
.series-detail-grid-item .series-detail-grid-right .notes-header {
    color: #9AAC0E;
}

/* Article list: "Started" indicator */
.article-instance-list-item-status.started {
    color: #FFF;
    background-color: #1E90FF;
    border: solid 1px #1E90FF;
}

/* Article: hiragana controls radio button */
.article-controls .radio.on {
    border: solid 0px #FFFFFF;
    border-radius: 50%;
    background: #777777;
}

/* Article: difficulty rating button */
.rating-button.on {
    background-color: #0078e7;
}

/* Article: remove background color from current sentence whose audio is being played */
.article-standard .paragraph.body .sentence-selected {
    color: inherit;
}

/* Article: tooltip heading */
.tooltip-content .heading {
    background-color: #0078e7;
}

/* Preferences: Headings */
.preferences h2 {
    color: #0078e7;
    border-bottom: solid 2px #0078e7;
}

/* Homepage: Horizontal line at top */
#top-image > div > hr {
    background: #1E90FF;
}

/* Reviews: question and answer cards */
.review-question-card,
.review-question-answer {
    background-color: #CCD4FF;
    border: solid 1px #0070FF;
}

/* Reviews: buttons */
.review-question-show-answer-button,
#review-question-delete-button {
    background-color: #0078e7;
}

}
