css:
@-moz-document url-prefix("https://inside.catlin.edu/scripts/sched/index.php?"), url-prefix("http://inside.catlin.edu/scripts/sched/index.php?") {
body {
    background: url("https://i.imgur.com/yWjwn3d.jpg");
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}

.times,
.specialday,
TD.daylabel,
TD.mainlabel,
td[bgcolor="#FFFFFF"] {
    background: rgba(255, 255, 255, 0.8);
}

td[bgcolor="#C0C0C0"] {
    background: rgba(200, 200, 200, 0.8);
}

TD.daylabel,
TD.mainlabel {
    border-style: solid;
    border-width: 2px;
    border-color: #000000;
}

HR.controls {
    display: none;
}

TD.controls.arrows a img {
    content: url("https://i.imgur.com/FBeFEE6.png") !important;
}

TD.controls.arrows:last-child a img {
    -moz-transform: scaleX(-1);
    -o-transform: scaleX(-1);
    -webkit-transform: scaleX(-1);
    transform: scaleX(-1);
    filter: FlipH;
    -ms-filter: "FlipH";
}

TD.controls.links a {
    color: black
}
}
