css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("electricimp.com"), url-prefix('https://electricimp.com/docs') {
  
  body {
    background-color: #111;
    color: #eee;    
  }
  
  h2, h3, h4 {
    color: #8FCC42;
    font-weight: normal;
    line-height: 130%;
  }
  
  .navbar {
    background-color: #222;
  }
  
  /* code regions */
  pre, pre code, code {
    background-color: #222;
    color: #ddd;
    font-family: Courier New;
  }
  
  /* parameter table alternate background color */
  table > tbody > tr:nth-child(2n+1) > td, table > tbody > tr:nth-child(2n+1) > th {
	background-color: #222;
}
 
  /* menu hover */
  .nav-stacked li > a:hover, .nav-stacked .nav-stacked a:hover {
	  background-color: #444;
    color: #eee;
  }
  
  /* selected menu item */
  .nav-stacked > li.active > a, .nav-stacked > li.active > a:hover, .nav-stacked > li.active > a:focus {
	 color: #EEE;
	 background-color: #648F2E;
  }
  
  .footer-default {
    background-color: #222;
  }
}
