css:
@-moz-document domain('sourceforge.net') {
	.CodeMirror-lines {
		font-family: "/*[[font]]*/", "Lucida Console", Monaco, monospace;
	}
}

@-moz-document domain('forum.userstyles.org'), domain('greasyfork.org') {
	#Form_Body {
		font-family: "/*[[font]]*/", "Lucida Console", Monaco, monospace;
	}
}

@-moz-document domain('app.simplenote.com') {
	.note #txtarea {
		font-family: "/*[[font]]*/", "Lucida Console", Monaco, monospace;
	}
}

@-moz-document domain('myjetbrains.com') {
	textarea.ring-input {
		font-family: "/*[[font]]*/", "Lucida Console", Monaco, monospace;
	}
}

@-moz-document domain('userstyles.org') {
	.CodeMirror * {
		font-family: inherit;
	}

	textarea {
		font-family: "/*[[font]]*/", "Lucida Console", Monaco, monospace;
	}
}

@-moz-document domain('p.dnnr.de') {
	.fd-zone {
		font-family: "/*[[font]]*/", "Lucida Console", Monaco, monospace;
	}
}

@-moz-document domain('trac.macports.org') {
	textarea {
		font-family: "/*[[font]]*/", "Lucida Console", Monaco, monospace;
	}
}
