css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.info-x.org") {
  body{font-size:1.2em;background: #171717!important;background-image: none!important;color:#999;}
  h1, h2{color:#ddd;}
  #page{font-size:1em;background-color: #171717;border: none;}
  .node{font-size: 100%;}
  /*left sidebar*/
  .region-sidebar-first .block .content{background: #171717;border: none;}
  li a.active{color: greenyellow;}
  .region-sidebar-first .block h2.title{background: none;border: none;}
  /*right sidebar*/
  .region-sidebar-second .block{background: none;border: none;}
  /*code*/
  div.geshifilter{background: #ddd;}
  /*header*/
  #header .section{background: #111;border:none;}
  /*footer*/
  #footer{background: #111;border:none;}
  /*form*/
  div.comment blockquote {background-color: #000;border: 1px solid #171717;padding: 8px;}
  .comment {border: 1px solid #171717;border-radius:0;}
  table.comment td.content{border-left: 1px solid #171717;}
  table.comment td.links{border-top: 1px solid #171717;}
}
