css:
@-moz-document domain("www.youtube.com") 
{
  #masthead-positioner
  {
    position: relative !important;
  }
  .appbar-hidden #masthead-positioner-height-offset
  {
    height: 0px !important;
  }
  
  /* Expand stage */
  
  .watch-stage-mode .player-height 
  {
    height: 576px !important;
  }
  
  .watch-stage-mode .player-width 
  {
    width: 1024px !important;
    left: -512px !important;
  }
  
  .watch-stage-mode #movie_player:not(.ytp-fullscreen) .video-stream.html5-main-video
  {
    width: 1024px !important;
    height: 576px !important;
  }
  
  /* New Youtube */
  
  #masthead-container
  {
    position: absolute !important;
  }
 
  ytd-watch[theater] #player-container
  {
    padding-top: 7px;
    padding-bottom: 7px;
  }
}
