css:
@-moz-document regexp("http://pokemonshowdown.com/damagecalc/"), regexp("pokemonshowdown.com/damagecalc/") {

html, body {
  background: -webkit-gradient(linear,left top,left bottom,from(grey),to(black)) 
        
}

fieldset {
    float: left;
     background: -webkit-gradient(linear,left top,left bottom,from(grey),to(black),to(black));
    border: 1px solid white;
}

.header {
    margin: 0 0 30px 0;
    background: #E5ECFB;
    background: -webkit-gradient(linear,left top,left bottom,from(#25282d),to(#5a5a5a));
}

.poke-info label {
    display: inline-block;
    width: 6em;
    color: white; 
    
}

html, body {
    font: 10pt Verdana,sans-serif;
    background-color: white;
    color: white;
    margin: 0;
    padding: 0;
    min-width: 100em;
}

a:-webkit-any-link {
    color: #1c2389;
    text-decoration: underline;
    cursor: auto;
}

a:-webkit-any-link:hover {
    color: #6eabb4;
    text-decoration: underline;
    cursor: auto;
}

textarea {
    -webkit-appearance: textarea;
    background-color: white;
    -webkit-rtl-ordering: logical;
    user-select: text;
    flex-direction: column;
    resize: auto;
    cursor: auto;
    white-space: pre-wrap;
    word-wrap: break-word;
    border-width: 1px;
    border-style: solid;
    border-color: blue;
    border-image: initial;
    padding: 2px;
}

textarea.click {
    -webkit-appearance: textarea;
    background-color: white;
    -webkit-rtl-ordering: logical;
    user-select: text;
    flex-direction: column;
    resize: auto;
    cursor: auto;
    white-space: pre-wrap;
    word-wrap: break-word;
    border-width: 1px;
    border-style: solid;
    border-color: red;
    border-image: initial;
    padding: 2px;
}



.poke-info .crit-btn {
    font-size: 0.8px;
    width: 2.5px;
    color: black;
    height: 1px;
    padding: 3px 3px;
}

.poke-info .z-btn {
    font-size: 0.8em;
    width: 1.5em;
    color: black;
    height: 1em;
    padding: 3px 3px;
}
}
