css:
@-moz-document domain("sf.taobao.com") {
.sf-pai-item-list .pai-item .link-wrap .header-section .title {
    overflow: visible !important;
    font-size: 10px !important;
    padding: 0 5px !important;
    width: 268px !important;
}
.price-todo,
.price-todo .value {
    color: #00BCD4 !important;
    font-weight: bold;
}
.price-done,
.price-done .value {
    color: #4CAF50 !important;
    font-weight: bold;
}
.price-current,
.price-current .value {
    color: #795548 !important;
    font-weight: bold;
}
.pai-status-doing .price-current,
.pai-status-doing .price-current .value {
    color: #F44336 !important;
    font-weight: bold;
}
.bid-tips {
    font-size: x-small;
}
.bid-tips .pai-xmpp-bid-count {
    font-size: large;
}
}
