css:
@-moz-document url-prefix("https://www.synchronkartei.de/") {
/* i really want this to be global */
}

@-moz-document url-prefix("https://www.synchronkartei.de/statistiken") {
/**
 * Build-Datum: 03.04.2017 23:14:27 CEST, Revision: c8c8994
 */
li:nth-child(1n) a.list-group-item {
	border-radius : 0px;
	border-bottom : 0px;
}
li:first-of-type:first-of-type a.list-group-item {
	border-top-right-radius    : 4px;
	border-top-left-radius     : 4px;
	border-bottom-right-radius : 0px;
	border-bottom-left-radius  : 0px;
	border-bottom              : 0px;
}
li:last-of-type:last-of-type a.list-group-item {
	border-top-right-radius    : 0px;
	border-top-left-radius     : 0px;
	border-bottom-right-radius : 3px;
	border-bottom-left-radius  : 3px;
	border-bottom              : 1px solid #ddd;
}
}

@-moz-document url-prefix("https://www.synchronkartei.de/wizard") {
/* wizard korrekturen */

/**
 * wizard unter menü punkte - START
 */
/* entfernung vom linken rand erhöhen */
.nav-pills>li:first-of-type {
	margin-left : 16px;   
}
/* rundung von unten entfernen */
.nav-pills>li>a {
	border-bottom-right-radius : 0px;
	border-bottom-left-radius  : 0px;
}
/**
 * wizard unter menü punkte - ENDE
 */
}
