css:
@-moz-document domain('forum.jogos.uol.com.br'){

/* Todo e rodapé (sem publicidade) */

.barrauol-bg{
	display: none;
}

div#publicidade{
	display: none;
}

div#footer{
	display: none;
}

div#importTab1{
	display: none;
}

input#search-field{
	display: none;
}

#search .search-submit-button{
	display: none;
}

.search-on-forum{
	display: none;
}

/*li{
	display: none;
}*/

div#content-imports .content{
	display: none;
}

.pluginFontHelvetica, .pluginFontHelvetica button, .pluginFontHelvetica input, .pluginFontHelvetica label, .pluginFontHelvetica select, .pluginFontHelvetica td, .pluginFontHelvetica textarea{
	display: none;
}

/* Bordas */

#content-inside{
	border-bottom: 4px solid #000;
}

/* Coloração */

body{
	background-color: #666 !important;
}

#content-inside{
	background-color: #3e3e3e;
}

table#foruns td, table#topics td, table#postsUser td{
	background-color: #151414;
	color: #fff !important;
}

/*table#foruns td, table#topics td, table#postsUser td{
	background-color: #000;
}*/

div#legendas{
	background-color: #000 !important;
	color: #fff;
}

table#foruns .topicos, table#foruns .mensagens, table#topics .topicos, table#topics .respostas, table#topics .autor, table#topics .exibicoes{
	color: #fff !important;
}

div#estatisticas .texto{
	background-color: #000;
}

div#contentDiv{
	background-color: #3e3e3e;
}

div#social-div{
	background: #000 !important;
	border: 1px solid #000; 
}

div#goto{
	background-color: #008aa7 !important;
}

div#content h1{
	color: #fff;
}

.postRow{
	background-color: #666;
	/*color: #fff;*/
}

.quote{
	background-color: #3e3e3e;
}

.topicRow{
	background-color: #666;
}

div#quickAnswerForm a:hover, .texto a, .post-assinatura a{
        color:#042aff;
}

#optionsButtonsBottom .optionMenu a{
 color:000;
}

.publishDate{
color:000;
}

.userLevel{
color:000;
}

}
