css:
@-moz-document domain("tinypng.com") {
body {
    color: #CCC;
    text-shadow: none;
}
body.home header {
    background: #222;
}
header nav {
    background: #111;
}
header nav ul {
    width: 90%;
    max-width: 90%;
}
header nav ul li > a {
    color: #CCC;
}
section.upload {
    max-width: 90%;
}
section.upload section.target {
    width: 90%;
    height: 30rem;
    color: #CCC;
}
section.upload figure.panda {
    left: 5%;
}
section.upload figure.bamboo {
    left: 90%;
}
section.upload figure.grass,
section.upload figure.cloud-one,
section.upload figure.cloud-two {
    display: none;
}
section.upload section.target figure.icon {
    margin-top: 10rem;
}
body.home figure.logo {
    position: absolute;
    top: 20rem;
    right: 0;
}
body.home main {
    background: #333;
}
h3 {
    color: #10aded;
}
p.quote::before {
    color: #10aded;
}
figure.pngcomparison {
    background: #111;
}
body.home main.customerlogos {
    background: #0d355a;
}
footer,
footer div.bottom {
    background: #111;
}
}
