css:
@-moz-document domain("github.com") {

.header {
  background-color: #ffffff;
}


.header-dark,
.header-nav-link {
    background-color: transparent !important;
    color: grey !important;
}
.header-dark .header-nav-link {
    color: black !important;
}
.header-logo-invertocat .octicon-mark-github {
    filter: invert(1) !important;
}
.header-logo-invertocat:hover .octicon-mark-github:hover {
    color: #bf873f !important;
}
.header-search-input {
    background-color: whitesmoke !important;
}
.header-search-input placeholder {
    color: black !important;
}
.header-dark .header-nav-link:hover,
.header-dark .header-nav-link:focus {
    color: grey !important;
}
.header-dark .header-search-input::-webkit-input-placeholder {
    color: grey !important;
}
.header-dark .header-search-input::-moz-placeholder {
    color: grey !important;
}
.header-dark .header-search-input:-ms-input-placeholder {
    color: grey !important;
}
.header-dark .header-search-input::placeholder {
    color: grey !important;
}
.header-dark .header-search-input {
    color: black !important;
}
.header-dark .header-nav-link:hover .dropdown-caret,
.header-dark .header-nav-link:focus .dropdown-caret {
    border-top-color: grey !important;
}
}
