css:
@-moz-document url-prefix("https://boards.4chan.org/mu/thread/") {
div.post div.file .fileThumb {
    float: right;
    margin-left: 20px;
    margin-right: 20px;
    margin-top: 3px;
    margin-bottom: 5px;
    box-shadow: -0.5px -0.5px 10px -12px #000000;
}
div.thread {
    margin: auto;
    clear: both;
    width: 1140px;
}
div.reply {
    background-color: #282a2e;
    border: 10px solid #282a2e;
    display: table;
    padding: 5px;
    margin: 20px;
    width: 1070px;
	box-shadow: -0.5px -0.5px 20px -6px #000000;
    transition: width 2s, height 4s;
}
body {
    background: rgb(38, 38, 49) none;
    color: #ffffff;
    font-family: tahoma,arial,helvetica,sans-serif;
    font-size: 10pt;
    margin-left: 0;
    margin-right: 0;
    margin-top: 5px;
    padding-left: 5px;
    padding-right: 5px;
}
div.thread>div:nth-of-type(2)>div.reply {
    margin-top: 200px !important;
}
div.post div.file .fileThumb img {
    float: left;
    margin-bottom: 20px;
    box-shadow: -0.5px -0.5px 10px -12px #000000;
}
#blotter {
    visibility: hidden;
}
#bannerCnt {
    border: 1px solid #000;
    margin: 5px auto;
    width: 0px;
    height: 100px;
    max-width: 100%;
    visibility: hidden;
}
.reply:target, .reply.highlight {
    background: #1D1D21!important;
    border: 10px solid #1d1d21!important;
    padding: 5px;
    margin: 20px;
    width: 1070px;
}
#quickReply {
    display: block;
    position: fixed;
    padding: 2px;
    font-size: 10pt;
    width: 300px;
    border: 0px;
}
hr.abovePostForm {
    width: 60px;
    height: 50px;
    display: block;
}
div.sideArrows {
    /* color: #c5c8c6; */
    /* float: left; */
    /* margin-right: 2px; */
    /* margin-top: 0px; */
    /* margin-left: 2px; */
    visibility: hidden;
}
hr.abovePostForm::before {
    content: '/mu/';
    font-family: Tahoma,sans-serif;
    font-size: 28px;
    font-weight: bold;
}
div.boardBanner>div.boardTitle {
    visibility: hidden;
}
#togglePostFormLink {
    visibility: hidden;
}
.UIMenu, .UIPanel {
    position: fixed;
    width: 250px;
    height: 100%;
    top: 0;
    left: 0;
    z-index: 9002;
}
div.post div.file {
    display: block;
}
div.reply:hover {
    background-color: #35373a;
    border: 10px solid #35373a;
}
#image-hover {
    position: absolute;
    max-width: 50% !important;
    max-height: 50% !important;
    top: 0px;
    float: left;
    z-index: 9002;
}
#quote-preview {
    display: block;
    position: absolute;
    top: 0;
    padding: 3px 6px 6px 3px;
    margin: 0;
    width: 600px;
}
div.post div.file .fileThumb img {
    max-width: 1070px !important;
    max-height: none !important;
    opacity: 0.75;
    transition: opacity 0.75s;
}
div.post div.file .fileThumb img:hover {
    opacity: 1.0;
    transition-timing-function: ease;
}
#yt-preview {
    position: absolute;
    width: 300px;
}
}

@-moz-document url("https://boards.4chan.org/mu/catalog") {
hr.abovePostForm {
    width: 60px;
    height: 50px;
    display: block;
}
hr.abovePostForm::before {
    content: '/mu/';
    font-family: Tahoma,sans-serif;
    font-size: 28px;
    font-weight: bold;
}
div.boardBanner>div.boardTitle {
    visibility: hidden;
}
#togglePostFormLink {
    visibility: hidden;
}
.UIMenu, .UIPanel {
    position: fixed;
    width: 250px;
    height: 100%;
    top: 0;
    left: 0;
    z-index: 9002;
}
#blotter {
    visibility: hidden;
}
#bannerCnt {
    border: 1px solid #000;
    margin: 5px auto;
    width: 0px;
    height: 100px;
    max-width: 100%;
    visibility: hidden;
}
.thread img {
    display: inline;
    box-shadow: 0.5px -0.5px 10px 1px #000000;
}
body {
    background: rgb(38, 38, 49) none;
    color: #ffffff;
    font-family: tahoma,arial,helvetica,sans-serif;
    font-size: 10pt;
    margin-left: 0;
    margin-right: 0;
    margin-top: 5px;
    padding-left: 5px;
    padding-right: 5px;
}
}
