css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("tatoeba.org") {
  
    /* Add background colors back into the logs */
    
    .sentence-update {
        background: #ffffe7 !important;
    }
    .sentence-obsolete {
        background: #eee !important;
    }
    .sentence-insert {
        background: #e7ffe7 !important;
    }
    .sentence-insert {
        background: #e7ffe7 !important;
    }
    .sentence-delete {
        background: #fcc !important;
    }
    .link-insert {
        background: #dff
    }
    .link-delete {
        background: #ffedcc
    }
    /* resize the avatar to something smaller - 20 match flag height */
    
    .md-avatar {
        width: 28px ! important;
        height: 28px ! important;
        border: 1px #888 solid;
        background: #eee;
    }
    /* move the 3 images closer together */
    
    md-button,
    md-list-item img {
        margin: 0px 0px 0px 0px ! important;
    }
    .md-avatar {
        margin: 8px 0px 0px 4px ! important;
        padding: 0px ! important;
    }
    .md-list-item-text {
        padding-left: 12px ! important;

    }
    /* remove some of the padding, so the page is more concise. */
    
    .md-2-line {
        padding: 0px 0px 0px 0px! important;
        border-top: 1px solid #999 ! important;
    }
    .md-secondary {
        margin: 8px 0px 0px 4px ! important;
        border: 1px solid black ! important;
    }
}
