css:
/* i really want this to be global */
.header-dark {
    background-color: #f5f5f5;
}

.header-dark .header-nav-link:hover, .header-dark .header-nav-link:focus {
	color: #555;
}

.header-dark .header-nav-link {
	color: rgb(85, 85, 85);
}

.header-dark .header-logo-wordmark, .header-dark .header-logo-invertocat {
    color: #555;
}

.header-search {
	width: 340px;
}

.header-dark .header-search-input {
	color: #333;
    border: 1px solid #ddd;
}

.header-dark .header-search-scope {
	color: #767676;
    font-size: 12px;
    border-right-color: #eee;
}

.header .header-search-wrapper.focus .header-search-scope {
	color: #767676;
    border-right-color: #eee;
}

.notification-indicator:hover .mail-status {
	border: none;
}

.header-dark .notification-indicator .mail-status {
	border: none;
}

.notification-indicator .mail-status {
	width: 10px;
    height: 10px;
}
