css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://eu.battle.net/forum"), url-prefix("https://eu.battle.net/forum"),
               url-prefix("http://us.battle.net/forum"), url-prefix("https://us.battle.net/forum"),
               url-prefix("http://kr.battle.net/forum"), url-prefix("https://kr.battle.net/forum"),
               url-prefix("http://sea.battle.net/forum"), url-prefix("https://sea.battle.net/forum") {
  .TopicPost-rank--down {
    opacity:0;
  }
  .TopicPost-rank--down:hover {
    opacity:1;
  }
}
