@-moz-document domain("unseen.is") {

body {font:normal 9pt/11pt Segoe UI, Calibri, Helvetica Neu, Helvetica, Arial, DejaVu Sans, Microsoft Yahei UI, sans!important}
.contact .name {font:bold 9pt/11pt Segoe UI, Calibri, Helvetica Neu, Helvetica, Arial, DejaVu Sans, Microsoft Yahei UI, sans!important;color:#fff!important}
.group .name {font:bold 9pt/11pt Segoe UI, Calibri, Helvetica Neu, Helvetica, Arial, DejaVu Sans, Microsoft Yahei UI, sans!important;color:#0ff!important}
.group .status-text, .contact .status-text {font:normal 9pt/11pt Segoe UI, Calibri, Helvetica Neu, Helvetica, Arial, DejaVu Sans, Microsoft Yahei UI, sans!important;color:#ddd!important}
h5.name {font:bold 9pt/12pt Segoe UI, Calibri, Helvetica Neu, Helvetica, Arial, DejaVu Sans, Microsoft Yahei UI, serif!important}
message {font:normal 11pt/12pt Cambria, Times New Roman, Times, DejaVu Serif, Microsoft Yahei UI, serif!important}
.cke_editable {font:normal 13pt/14pt Cambria, Times New Roman, Times, DejaVu Serif, Microsoft Yahei UI, serif!important}

}
