css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("strawpoll.com"), domain("strawpoll.de") {

	*, #logo {
		color: #F1F1F1;
	}

	a, a:visited {
		color: #DE5231;
	}

	h1, h2, h3, h4, h5, h6 {
		color: #7a98c0;
	}

	html, body {
		background-color: #161616;
	}

	.answer input, input.title {
		color: #F1F1F1;
		background-color: #292929;
	}

	#footer, .footer, .container, .container.box, .box-title {
		background-color: #292929;
	}

	.header {
		background-color: #292929;
		box-shadow: 0 1px 1px rgba(255, 255, 255, .1);
	}

	#footer {
		border-color: #353535;
	}

	.bg-content .container{
		background-color: transparent;		
	}

	.container.box.box-orange {
		border-top: 8px solid #DE5231;
	}

	/* pikaday.js */

	.pika-single {
		background-color: #292929;
	}

	.pika-label {
		background-color: transparent;
	}

	.pika-button:hover {
		border-radius: initial;
	}

	.pika-select option {
		background-color: #161616;
	}

}
