css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://arstechnica.com/index.ars")
{
  #Masthead, #Banner, #Footer, #Sidebar, #Main #Content .Bubbles {display:none!important;}
  .ContentHeader {display:none!important;}
  .Content.Home, .ContentFooter {background:white!important;}
  #Page {width:99%!important;}
  #Content {width:100%!important;}
  .Content, .Content .ContentBody, .Content .ContentFooter {margin:0px!important;}
  #Page, .Content, .Content .ContentFooter {padding:0px!important;}
  .Content {margin-right:0px!important;}
  .Content .ContentBody {padding:10px 0px 0px 10px!important;}
}

@-moz-document url-prefix("http://arstechnica.com/articles")
{
  #Masthead, #Banner, #Footer, #Sidebar {display:none!important;}
  .Tags, .PostOptions a img {display:none!important;}
  #Page, .PostOptions {width:99%!important;}
  #Content {width:100%!important;}
  .Content.Article .ContentHeader{background:#393C39!important;}
  #Page, .PostOptions, .Paging, .Content .ContentFooter {padding:0px!important;}
  .PostOptions, .Paging, .Content .ContentFooter, .Content .ContentBody {margin:0px!important;}
  .Content {margin-right:0px!important;}
  .Content .ContentFooter p.Paging {padding-top:0px!important;}
  .Content .PostOptions p.Paging {padding-right:0px!important;}
  .Content.Article .ContentFooter {padding-bottom:0px!important;}
  .ContentFooter, .PostOptions {background:#EEEEEE!important;}
}

@-moz-document url-prefix("http://arstechnica.com/news.ars")
{
  #Masthead, #Banner, #Footer, #Sidebar, #LatestPosts {display:none!important;}
  .Content .PostOptions.flat, .Tags {display:none!important;}
  #Page {width:99%!important;}
  #Content {width:100%!important;}
  #Page {padding:0px!important;}
  .Content {padding-right:0px!important;}
  .ContentHeader {background:#424542!important;}
  .ContentHeader .Replace {display:block!important;color:#FFFFFF!important;}
  .ContentFooter {background:#4A4942!important;}
  #Content {background:#EEEEEE!important;}
}

@-moz-document url-prefix("http://arstechnica.com/guides")
{
  #Masthead, #Banner, #Footer, #Sidebar {display:none!important;}
  .Tags, .PostOptions a img {display:none!important;}
  #Page {width:99%!important;}
  #Page {padding:0px!important;}
  #Content, .PostOptions {width:100%!important;}
  .ContentHeader {background:#424542!important;}
  .ContentFooter {background:#FFFFFF!important;}
  .Content.Article .ContentFooter {background:none!important;padding-bottom:0px!important;}
}

@-moz-document url-prefix("http://arstechnica.com/journals")
{
  #Masthead, #Banner, #Footer, #Sidebar {display:none!important;}
  .Tags, .PostOptions a img {display:none!important;}
  #Page {width:99%!important;}
  #Page {padding:0px!important;}
  #Content, .PostOptions, .Content.Journal {width:100%!important;}
  .Content .ContentBody {margin:0px!important;padding:0px!important;}
  .PostOptions {background:none!important;}
  h3.Date {background:#9D9A95!important;}
  .Content {margin-right:0px!important;padding:0px!important;}
  .ContentHeader {margin:0px!important;}
}
