css:
@-moz-document url-prefix("https://www.2dfan.") {
body {
    background-color:#000;
}
.hl-bg0 {
    background-color:#000;
}
.hl-bg1 {
	background-color:#000;
}
.block {
    background:#222;
}
.block-header .title{
    color:#fff;
}
.site-search {
    background-color:#06253a;
}
.navbar>.navbar-inner {
    background-image:linear-gradient(to bottom, #6a91ff, #06253a);
    border:1px solid #06253a;
}
.navbar .nav>li>a {
    color:#06c9f8;
    text-shadow:0 1px 0 #004150;
}
.navbar .nav>.active>a, .navbar .nav>.active>a:hover, .navbar .nav>.active>a:focus {
    background-color:#6a91ff;
    color:#fff;
}
.row-fluid>.block{
    color:#f89406;
}

.block>.well {
    visibility:hidden;
    background:#222;
}
.well {
    background:#222;
    border:1px solid #000;
}

.pagination>ul>li>a:hover {
    ;background:#000;
}

.pagination>ul>li>a:link {
    background:#333;
}

.pagination>ul>li>a:active {
    background:#06253a;
}

.pagination>ul>li>a:visted {
    background:#06253a;
}

.form-actions{
    background-color:#333333;
}


}
