css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url("chrome://speeddial/content/speeddial.xul") {
  :root,
  .sd-tab {
    background: #383838;
    color: grey;
  }
  .sd-tab,
  .sd-tabs-right,
  .sd-tabs-left,
  .sd-tabpanels,
  #speeddialTabbox-tabs,
  #speeddialTabbox-panelContainer{
    border: 0px solid transparent !important;
  }
  #speeddialTabbox-tabs{

    box-shadow:inset 0px 5px 8px -3px rgba(0,0,0,1) !important;
  }
  .thumbnail:hover {
    /*opacity: 0.7;*/
  }
  .speeddial-top-middle,
  .speeddial-top-left,
  .speeddial-top-right,
  .speeddial-middle-left,
  .speeddial-middle-middle,
  .speeddial-middle-right,
  .speeddial-bottom-left,
  .speeddial-bottom-middle,
  .speeddial-bottom-right
  {

  }
  .speeddialThumb{
    background: #383838!important;
    border:1px solid #242424!important;
    box-shadow:0 0 5px #000!important;
    padding-left: 5px;
  }
  .speeddialThumb:hover{
   background: #242424 !important;
  }
  .speeddial-top-left,/*.speeddial-top-middle,*/.speeddial-top-right,
  .speeddial-middle-left,.speeddial-middle-middle,.speeddial-middle-right,
  .speeddial-bottom-left,.speeddial-bottom-middle,.speeddial-bottom-right{
    position:absolute!important;display:none!important;
  }
  .speeddial-top-middle{
    background:none!important;
  }
}
