css:
@-moz-document domain("twitter.com") {
    .content-main {
        margin: auto;
        float: inherit;
    }
    @media (min-width: 1236px) {
        body.three-col .content-main {
            margin: auto;
            float: inherit;
        }
    }
    .dashboard {
        display: none;
    }
    .wrapper,
    .wrapper-narrow,
    .wrapper-permalink {
        width: 100%;
    }
}
